import React from 'react'
import ReactDOM from "react-dom"
import Select from "react-select"
import styled from "styled-components"


export default function CreateTicketModal(props) {
    // inputs needed
    // title 
    // description 
    // drop down ticket type
    // drop down ticket status
    // drop down ticket priority
    // user name
    const priority = [
        { value: "lowest", label: "Lowest" },
        { value: "low", label: "Low" },
        { value: "medium", label: "Medium" },
        { value: "high", label: "High" },
        { value: "highest", label: "Highest" }
    ]

    const status = [
        { value: "open", label: "Open" },
        { value: "resolved", label: "Resolved" },
        { value: "closed", label: "Closed" },
        { value: "reopened", label: "Reopened" },
        { value: "in progress", label: "In Progress" },
    ]

    const type = [
        { value: "bug", label: "Bug" },
        { value: "new feature", label: "New Feature" },
        { value: "improvement", label: "Improvement" },
        { value: "task", label: "Task" },
        { value: "testing", label: "Testing" },
    ]

    const customStyles = {
        option: (provided, state) => ({
            ...provided,
            borderBottom: '1px dotted pink',
            color: state.isSelected ? 'red' : 'blue',
            padding: 20,
        }),
        control: () => ({
            // none of react-select's styles are passed to <Control />
            width: 300,
        }),
        singleValue: (provided, state) => {
            const opacity = state.isDisabled ? 0.5 : 1;
            const transition = 'opacity 300ms';

            return { ...provided, opacity, transition };
        }
    }

    const { closeModal } = props
    const modal = (
        <>
            <Overlay />
            <ModalWrapper >
                <button onClick={closeModal}>X</button>
                <h1>Create A Ticket</h1>
                <input type="text" name="" id="" placeholder="Title" />
                <Select
                    styles={customStyles}
                    options={priority}


                />
                <Select options={status} />
                <Select options={type} />

            </ModalWrapper>
        </>
    )
    return ReactDOM.createPortal(modal, document.getElementById("portal"))
}

const Overlay = styled.div`
position: fixed;
top: 0;
left: 0;
width: 100%;
height: 100%;
background-color: rgba(255, 255, 255, .6);
-webkit-backdrop-filter: blur(2px);
backdrop-filter: blur(2px);
 
`
const ModalWrapper = styled.div` 
position: absolute;
top: 10%;
left: 25%;
width: 50rem;
height: 30rem;
background:#128DEB;
border-radius: 10px;
`
