import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import TicketDetail from "./TicketDetail"

export default function TicketList() {
  const [tickets, updateTickets] = useState([])
  const [detailTicket, updateDetailTicket] = useState("0")
  useEffect(() => {
    async function fetchTickets() {
      const response = await fetch("/api/v1/tickets")
      const json = await response.json()
      updateTickets(json.data)
    }
    fetchTickets()
  }, [])

  return (
    <Wrapper>
      <div>
        <TableTitle>
          <h3>Tickets</h3>
          <button type="submit">Create A Ticket</button>
        </TableTitle>
        <TableHeader>
          <ul>
            <div>
              <li>Title</li>
            </div>
            <div>
              <li>Type</li>
              <li>Status</li>
              <li>Priority</li>
            </div>
          </ul>
        </TableHeader>
        {
          tickets.map((ticket, event) => (
            <ListInfo key={ticket._id} onClick={() => updateDetailTicket(event)}>
              <Left>
                <p>{ticket.title}</p>
                <p>{ticket.description}</p>
              </Left>
              <Right>
                <p>{ticket.ticketType}</p>
                <p>{ticket.ticketStatus}</p>
                <p>{ticket.ticketPriority}</p>
              </Right>
            </ListInfo>
          ))
        }
      </div>
      {console.log(tickets[0])}
      <TicketDetail key={tickets._id} data={tickets[detailTicket]} />
    </Wrapper>
  );
}

const Wrapper = styled.div` 
display: flex;
background: white;
grid-area: ticketarea;
height: calc(100vh - 4.25rem);
`

const TableHeader = styled.div` 
background: #ddd;

ul{
display: flex;
justify-content: space-between;
}
div:nth-child(1){
  padding-top: .5rem;
padding-left: .9rem;
}
div:nth-child(2) {
  display: flex;
  padding-right: .25rem;
  li{
    padding: .5rem;
  }
}
`


const ListInfo = styled.div`
display: flex;
justify-content: space-between;
width: 100%;
padding: .5rem .75rem;
border-bottom: solid 1px #ccc;
`;

const Left = styled.div`
display: flex;
flex: 2;
flex-direction: column;
p:nth-child(1){
  font-weight: bold;
}
p {
padding: .25rem;
}
`;

const Right = styled.div`
display: flex;
flex: 1;
width: 500px;
justify-content: end;

p {
padding: .5rem;
}
`;

const TableTitle = styled.div`
display: flex;
justify-content: space-between;
padding: 1rem 1rem;
border-bottom: solid 1px #ccc;

button {
  padding: .5rem;
}
`;
