import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

export default function TicketTable() {
  const [tickets, updateTickets] = useState([])
  useEffect(() => {
    async function fetchTickets() {
      const response = await fetch("/api/v1/tickets")
      const json = await response.json()
      console.log(json.data)
      updateTickets(json.data)
    }
    fetchTickets()
  }, [])
  // const tickets = [
  //   {
  //     "ticketPriority": "Lowest",
  //     "ticketStatus": "Open",
  //     "ticketHistory": [
  //       {
  //         "created": "2020-11-30T01:40:20.971Z",
  //         "_id": "5fc44ebf101ed201944819f0",
  //         "user": "Jane Smith",
  //         "propertyChanged": "Submitter",
  //         "oldValue": "John Doe",
  //         "newValue": "Jane Smith",
  //         "__v": 0
  //       }
  //     ],
  //     "ticketComment": [
  //       "5fc44c65c8df4b5b2c31ead9",
  //       "5fc44c65c8df4b5b2c31eada"
  //     ],
  //     "createdOn": "2020-11-28T05:31:29.657Z",
  //     "_id": "5fc1e0be4b00cb54e0547fc4",
  //     "title": "Make sure things work 4",
  //     "description": "This is a ticket to make sure things work out.",
  //     "assignedDev": "John Doe",
  //     "submitter": "John Doe",
  //     "ticketType": "Bug",
  //     "__v": 4
  //   },
  //   {
  //     "ticketPriority": "Lowest",
  //     "ticketStatus": "Open",
  //     "ticketHistory": [],
  //     "ticketComment": [],
  //     "createdOn": "2020-11-28T05:31:29.657Z",
  //     "_id": "5fc1e0c04b00cb54e0547fc5",
  //     "title": "Make sure things work 4",
  //     "description": "This is a ticket to make sure things work out.",
  //     "assignedDev": "John Doe",
  //     "submitter": "John Doe",
  //     "ticketType": "Bug",
  //     "__v": 0
  //   },
  //   {
  //     "ticketPriority": "Lowest",
  //     "ticketStatus": "Open",
  //     "ticketHistory": [],
  //     "ticketComment": [],
  //     "createdOn": "2020-11-28T05:31:29.657Z",
  //     "_id": "5fc1e0c14b00cb54e0547fc6",
  //     "title": "Make sure things work 4",
  //     "description": "This is a ticket to make sure things work out.",
  //     "assignedDev": "John Doe",
  //     "submitter": "John Doe",
  //     "ticketType": "Bug",
  //     "__v": 0
  //   }
  // ];
  return (
    <Table>
      <thead>
        <tr>
          <th>Title</th>
          <th>Description</th>
          <th>Assigned Developer</th>
          <th>Submitter</th>
          <th>Ticket Priority</th>
          <th>Ticket Status</th>
          <th>Ticket Type</th>
        </tr>
      </thead>
      <tbody>
        {tickets.map((ticket) => (
          <tr key={ticket.id}>
            <td>{ticket.title}</td>
            <td>{ticket.description}</td>
            <td>{ticket.assignedDev}</td>
            <td>{ticket.submitter}</td>
            <td>{ticket.ticketPriority}</td>
            <td>{ticket.ticketStatus}</td>
            <td>{ticket.ticketType}</td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
}

const Table = styled.table`
border-collapse: collapse;
width: 100%;
grid-area: ticketarea;

thead th {
    padding: 0.5rem;
    text-align: left;
    border-bottom: solid 1px black;
}
tbody td {
  padding: 0.5rem;
  text-align: left;
  border-bottom: solid 1px black;
}
`;
