import React from 'react'
// import styled from "styled-components"


export default function Modal(props) {
    const { show, closeModal } = props

    return (
        <>
            <div className={show ? "modal" : "hide"}>
                <button onClick={closeModal}>X</button>
                <h1>Modal Heading</h1>

            </div>
        </>
    )
}


