  }
  span{
    cursor: pointer;
    margin-right: 2rem; 
    padding: .5rem;
    color: white;
    background: #EB7012;
    border-radius: 5px;
  }
}

.description{
  padding-top: .5rem;
}
.infoItem{
  ${'' /* margin: .5rem 0; */}
  padding: .5rem;
  
 p:nth-child(1) {
   text-transform: uppercase;
   color: #ABB1B6;
   font-weight: 500;
   padding-bottom: .25rem;
 }
}
.info{
  display: flex;
  flex-direction: column-reverse;
  justify-content: space-between;
  border-bottom: solid 1px #ddd;
  margin: 0 1rem;
  div{
    display: flex;
    justify-content: space-between;
  }
  select{
    border: 1px solid black;
  }
  .submitBtn{
    width: 8rem;
    padding: .5rem;
    margin: .5rem;
    background: #EB7012;
    color: white;
    border-radius: 5px;
}
}
