import React, { useState } from 'react'
import ReactDOM from "react-dom"
import styled from "styled-components"


export default function CreateTicketModal(props) {
    // inputs needed
    // title 
    // description 
    // drop down ticket type
    // drop down ticket status
    // drop down ticket priority
    // user name
    const [priority, setPriority] = useState()
    const [type, setType] = useState()
    function handleChange() {
        console.log()
    }



    const { closeModal } = props
    const modal = (
        <>
            <Overlay />
            <ModalWrapper >
                <button onClick={closeModal}>X</button>
                <h1>Create A Ticket</h1>
                <form>
                    <input type="text" name="" id="" placeholder="Title" />
                    <input type="text" name="" id="" placeholder="Description" />
                    <label>
                        Ticket Priority
          <select value={setPriority} onChange={handleChange}>
                            <option value="lowest">Lowest</option>
                            <option value="low">Low</option>
                            <option value="medium">Medium</option>
                            <option value="high">High</option>
                            <option value="highest">Highest</option>
                        </select>
                    </label>
                    <label>
                        Ticket Type
          <select value={setType} onChange={handleChange}>
                            <option value="bug">Bug</option>
                            <option value="new feature">New Feature</option>
                            <option value="improvement">Improvement</option>
                            <option value="task">Task</option>
                            <option value="testing">Testing</option>
                        </select>
                    </label>
                    <button>Submit</button>
                </form>


            </ModalWrapper>
        </>
    )
    return ReactDOM.createPortal(modal, document.getElementById("portal"))
}

const Overlay = styled.div`
position: fixed;
top: 0;
left: 0;
width: 100%;
height: 100%;
background-color: rgba(255, 255, 255, .6);
-webkit-backdrop-filter: blur(2px);
backdrop-filter: blur(2px);
 
`
const ModalWrapper = styled.div` 
position: absolute;
top: 10%;
left: 25%;
width: 50rem;
height: 30rem;
background:#128DEB;
border-radius: 10px;

form{
    display: flex;
    flex-direction: column;
    align-items: center;
}

input[type=text]{
    width: 50%;
    padding: 1rem;
    margin: .25rem;
}

select{
    padding: 1rem;
    margin: .25rem;
}
`
