import React, { Component } from 'react'
import styled from "styled-components"

export default class TicketDetail extends Component {
  constructor(props) {
    super(props)
    console.log(this.props)
  }
  render() {
    return (
      <Main>
        <TicketInfo>
          {/* <h2>{data.title}</h2> */}
          <Info>
            <div>
              <InfoItem>
                <p>Project</p>
                {/* <p>{tickets.data.project.title}</p> */}
              </InfoItem>
              <InfoItem>
                <p>Assigned Dev</p>
                {/* <p>{tickets.assignedDev}</p> */}
              </InfoItem>
              <InfoItem>
                <p>Created By</p>
                {/* <p>{tickets.submitter}</p> */}
              </InfoItem>

            </div>
            <div>
              <InfoItem>
                <p>Type</p>
                {/* <p>{tickets.ticketType}</p> */}
              </InfoItem>
              <InfoItem>
                <p>Status</p>
                {/* <p>{tickets.ticketStatus}</p> */}
              </InfoItem>
              <InfoItem>
                <p>Priority</p>
                {/* <p>{tickets.ticketPriority}</p> */}
              </InfoItem>
            </div>
          </Info>
          {/* <Description>{tickets.description}</Description> */}
        </TicketInfo>
        <div>
          {/* {tickets
          .map((ticket) => {
            if ('comment' in ticketItem) {
              return (
                <TicketComment>
                  <div>
                    {ticket.commenter}
                    {' '}
                  commented
                  {' '}
                    <span>

                      {DateDiff(
                        ticket.createdAt,
                      )}
                    </span>
                  </div>
                  <p>
                    {ticketItem.comment}
                  </p>
                </TicketComment>
              );
            }
            if ('oldValue' in ticketItem) {
              return (
                <TicketChange>
                  <p>
                    {ticketItem.user}
                    {' '}
                  changed
                  {' '}
                    {ticketItem.propertyChanged}
                    {' '}
                  from
                  {' '}
                    {ticketItem.oldValue}
                    {' '}
                  to
                  {' '}
                    {ticketItem.newValue}
                    <span>
                      {DateDiff(
                        ticketItem.createdAt,
                      )}
                    </span>
                  </p>

                </TicketChange>
              );
            }
            return null;
          })} */}
        </div>
        <TicketCommentBox>
          <textarea name="" id="" cols="30" rows="10" />
          <button type="submit">Submit</button>
        </TicketCommentBox>
      </Main>
    )
  }



}




const Main = styled.div`
background: white;
width: 100%;
grid-area: ticketarea;
`

const TicketInfo = styled.div` 
margin: .5rem;
h2{
padding: 0.5rem 0;
}
`;

const Description = styled.p` 
padding-top: .5rem;
`;

const Info = styled.div`
display: flex;
justify-content: space-between;
border-bottom: solid 1px #ddd;
`;

const InfoItem = styled.section`
margin: .5rem 0;
 p:nth-child(1) {
   text-transform: uppercase;
   color: #ABB1B6;
   font-weight: 500;
   padding-bottom: .25rem;
 }
`;


const TicketCommentBox = styled.div`
  display: flex;
  flex-direction: column;
  width: 40rem;
  margin: 0 auto ;

input[type=text] {
  
  height: 5rem;
  border: solid 1px black;
}

textarea {
  border: solid 1px black;
}

button {
margin-top: .5rem;
padding: .5rem;
width: 6rem;

}
`;

const TicketChange = styled.div`
display: flex;
justify-content: space-between;
background: #EFF3F6;
margin: .5rem;
padding: .5rem;

span {
  padding-top: .25rem;
  padding-left: .25rem;
    font-size: 12px;
    color: #B2B9BE; 
}
`;

const TicketComment = styled.div`
display: flex;
flex-direction: column;
padding: .5rem;

div {

    padding: .5rem;
    background: #EFF3F6;
}
span {
  padding-top: .25rem;
    font-size: 12px;
    color: #B2B9BE; 
}
p {
    margin-top: .25rem;
    padding: .75rem;
    border-left: solid 1px #ccc;
}
`;
