import React, { Component } from 'react'
import styled from "styled-components"
import Portal from "./Portal"

export default function Modal(props) {


    return (
        <Portal>
            <ModalWrapper>
                <button >X</button>
                <div>{this.props.children}</div>
            </ModalWrapper>
        </Portal>
    )
}


const ModalWrapper = styled.div` 
position: absolute;
top: 1rem;
left: 1rem;
background: red;
`
